# Temporary storage for user input
user_name: .space 50
user_nationality: .space 50

.text
.globl main
main:
    # Print main prompt
    li $v0, 4
    la $a0, main_prompt
    syscall

    # Get user's name
    li $v0, 4
    la $a0, name_prompt
    syscall

    li $v0, 8              # Read string syscall
    la $a0, user_name
    li $a1, 50             # Maximum string length
    syscall

    # Get user's nationality
    li $v0, 4
    la $a0, nationality_prompt
    syscall

    li $v0, 8
    la $a0, user_nationality
    li $a1, 50
    syscall

    # Initialize loop
    li $t6, 1              # Continue flag (1 means continue)

do_while_loop:
    # Prompt for source currency
    li $v0, 4
    la $a0, src_prompt
    syscall

    li $v0, 5              # Read integer syscall
    syscall
    move $t0, $v0          # $t0 = fromCurrency

    # Prompt for destination currency
    li $v0, 4
    la $a0, dest_prompt
    syscall

    li $v0, 5
    syscall
    move $t1, $v0          # $t1 = toCurrency

    # Validate inputs
    blt $t0, 1, invalid_input
    bgt $t0, 45, invalid_input
    blt $t1, 1, invalid_input
    bgt $t1, 45, invalid_input

    # Prompt for amount
    li $v0, 4
    la $a0, amt_prompt
    syscall

    li $v0, 6
    syscall
    mov.s $f0, $f0         # $f0 = amount

    # Conversion logic
    la $a0, conversion_rates
    sub $t2, $t0, 1
    sub $t3, $t1, 1
    li $t4, 45
    mul $t5, $t2, $t4
    add $t5, $t5, $t3
    sll $t5, $t5, 2
    add $a0, $a0, $t5
    lwc1 $f1, 0($a0)
    mul.s $f2, $f0, $f1

    # Print result
    li $v0, 4
    la $a0, result_msg
    syscall

    li $v0, 2
    mov.s $f12, $f2
    syscall

    # Ask to continue
    li $v0, 4
    la $a0, continue_prompt
    syscall

    li $v0, 5
    syscall
    move $t6, $v0
    bne $t6, 1, exit_loop

    j do_while_loop

exit_loop:
    # Print summary
    li $v0, 4
    la $a0, user_details
    syscall

    la $a0, user_name
    syscall

    li $v0, 4
    la $a0, nationality_details
    syscall

    la $a0, user_nationality
    syscall

    li $v0, 4
    la $a0, end_prompt
    syscall

    li $v0, 10
    syscall

invalid_input:
    li $v0, 4
    la $a0, invalid_msg
    syscall

    j do_while_loop
